/*************************************
* DEPENDENCIES
*************************************/

var should = require('chai').should();
var tools = require('../lib/tools');

/*************************************
* TESTS
*************************************/

describe('tools', function() {

  describe('str2bin', function() {
    it('should convert string to bytes correctly', function() {
      tools.str2bin('ABC').should.equal('010000010100001001000011');
    });
  });

  describe('bin2str', function() {
    it('should convert bytes to string correctly', function() {
      tools.bin2str('010000010100001001000011').should.equal('ABC');
    });
  });

  describe('int2str', function() {
    it('should convert integer to bytes correctly', function() {
      tools.int2bin(2).should.equal('10');
    });
    it('should return NaN for incorrect parameter', function() {
      tools.int2bin('aa').should.be.NaN;
    });
  });

  describe('str2int', function() {
    it('should convert bytes to integer correctly', function() {
      tools.bin2int('10').should.equal(2);
    });
    it('should return NaN for incorrect parameter', function() {
      tools.bin2int('aa').should.be.NaN;
    });
  });

  describe('padStart', function() {
    it('should complete a string correctly', function() {
      tools.padStart('1111', 8, '0').should.equal('00001111');
    });
    it('should not modify the string with invalid count paremeter', function() {
      tools.padStart('1111', 2, '0').should.equal('1111');
    });
  });
});